import React, { Component } from "react";
import TemperatureInput from "./TemperatureInput";

class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tem: '',
      scale: 'c'
    };
    this.handleCelsiusChange = this.handleCelsiusChange.bind(this);
    this.handleFahChange = this.handleFahChange.bind(this);
  }

  handleCelsiusChange(tem) {
    this.setState({
      scale:'c',
      tem
    })
  }

  handleFahChange(tem) {
    this.setState({
      scale: 'f',
      tem
    })
  }

  render() {
    // const {scale} = this.props;
    const scaleNames = {
      c: 'Celsius',
      f: 'Fahrenheit'
    }
    const {tem} = this.state;
    // const cel = scale === 'c'

    return (
     <div>
      <TemperatureInput 
        scale = 'c'
        onTemChange = {this.handleCelsiusChange} />
      <TemperatureInput 
        scale = 'f'
        onTemChange = {this.handleFahChange}/>
     </div>
    );
  }
}

export default Calculator;
