import React, { Component } from "react";
import TemperatureInput from "./TemperatureInput";

class Calculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      temp: '',
      scale: 'c'
    };
    this.handleCelsiusChange = this.handleCelsiusChange.bind(this);
    this.handleFahChange = this.handleFahChange.bind(this);
  }

  handleCelsiusChange(temp) {
    this.setState({
      scale:'c',
      temp
    })
  }

  handleFahChange(tem) {
    this.setState({
      scale: 'f',
      tem
    })
  }

  toCelsius(fah) {
    return (fah - 32) * 5 /9;
  }

  toFah(cel) {
    return (cel * 9/5) + 32;
  }

  tryConvert(temp, convert) {
    const input = parseFloat(temp);
    if(Number.isNaN(input)) {
      return '';
    }
    const output = convert(input);
    const rounded = Math.round(output *1000)/ 1000;
    return rounded.toString();
  }

  render() {
    const {scale} = this.props;
    const {temp} = this.state;
    const cel = scale === 'f' ? this.tryConvert(temp, this.toCelsius): temp;
    const fah = scale === 'c'? this.tryConvert(temp, this.toFah): temp

    return (
     <div>
      <TemperatureInput 
        scale = {scale.c}
        temp = {cel}
        onTemChange = {this.handleCelsiusChange} />
      <TemperatureInput 
        scale = {scale.f} 
        temp = {fah}
        onTemChange = {this.handleFahChange}/>
     </div>
    );
  }
}

export default Calculator;
