import React, { Component } from "react";


class SearchBar extends Component {
  constructor(props){
    super(props);
    this.state = {
      searchWords: "",
      // item : 0
    };
  }

  handleFilter = (e) => {
    const searchWords = e.target.value;
    this.setState({
      searchWords
    });
  }

  render() {
    const {filter} = this.props;
    return (
      <form>
        <input 
          className='input'
          type="text" 
          placeholder = 'filter lists'
          value = {filter}
          onChange = {this.handleFilter}
        />
      </form>
    )
  }
}

export default SearchBar;
