import React, { Component } from "react";

class Action extends Component {
  constructor(props) {
    super(props);
    this.state = {
      item: 0
    }

  }

  handleInsert = () => {
    this.props.insertTask(this.state.item);
    this.setState({
      item: this.state.item +1
    })

  }
  render() {
    return (
      <div>
        <button onClick = {() => {this.props.clearList()}}>Clear the list</button>
        <button onClick = {() => {this.props.resetList()}}>Reset the list</button>
        <button onClick = {this.handleInsert}>Insert one task</button>
      </div>
    )
  }
}

export default Action;
