import React, { Component } from "react";

class TaskTable extends Component {
  render() {
    const { tasks } = this.props;

    return (
      <div>
        {tasks.map((task, i) => {
          return (
            <tr>
              <td className="taskNumber">{i+ 1}</td>
              <td className="taskName">{task}</td>
              <td className="taskDel">
                <button>x</button>
              </td>
            </tr>
          );
        })}
      </div>
    );
  }
}

export default TaskTable;
